import React, { Component } from 'react';

// 引入的页面重复引入了Component，造成引入失败
export default (ComposedComponent) => class extends Component {
    constructor(props) {
        super(props);
        this.state = {
            data: this.props.data
        }
        this._act = this._act.bind(this)
    }

    _act(data){
        this.setState(data);
    }

    componentWillMount() {
        if( this.props.name )
            SAX.setter( this.props.name, null, this._act )
    }

    render() {
        return (
            <ComposedComponent {...this.props} data={this.state.data} />             
        )
    }
};
